	$(document).ready(function () {

    var length = $('#right-sidebar').height() + 55;
    var noFooterLength = $('.blog').height() + 30;


    $(window).scroll(function () {

        var scroll = $(this).scrollTop();
        var FooterHeight = $('#bsocialsnbt').height() + "px";
        var follows = scroll - (noFooterLength);

        if (scroll > length) {
             $('#right-sidebar').addClass('dockTo-top');
        } else {         
	        $('#right-sidebar').removeClass('dockTo-top');
        }

        if (scroll > noFooterLength){
        	$('#right-sidebar').addClass('dockTo-bottom');
        	$('.dockTo-bottom').css('bottom', follows);
		} else{         
	        $('#right-sidebar').removeClass('dockTo-bottom');
	        $('#right-sidebar').css('bottom', '');
        }
    });
});
