  	$.mobile = function() {
		$(".navbar-brand").hide()
		$(".dark-overlay").css("height", "300px")
		$("#blognbt").css("height", "300px")
        $(".navbar-default").css("background-color", "transparent")
        $(".site-title").css("font-size", "36px")
        $(".vision-border").css({"width":"100%", "top":"30%"})
        $(".post-title").css("font-size", "30px")
	}
	$.fullscreen = function() {
		$(".dark-overlay").css("height", "280px")
		$("#blognbt").css("height", "280px")
		$(".vision-border").css("top","15%")
	}

	$(document).ready(function() {
		if ( $(window).width() > 400) {     
	        $.fullscreen()
        }
        else {
          $.mobile()
        }
	});

	$(window).on('resize', function(){
		if ( $(window).width() > 400) {     
	        $.fullscreen()
        }
        else {
          $.mobile()
        }
	});

